mapper.insert:
	insert into subcategory(TOPCATEGORY_ID
									 ,SUBNAME
									 ,LAST_MODI_YMD
									)
 		values(
 									#{topcategory.topcategory_id}
 								   ,#{subname}
 								   ,now()
 		);

 		select last_insert_id()

mapper.update:
update subcategory
						set
							 TOPCATEGORY_ID = #{topcategory.topcategory_id}
							,SUBNAME = #{subname}
							,LAST_MODI_YMD = now()
 where SUBCATEGORY_ID = #{subcategory_id}
